pterView<?> parent, View view,int position, long id) {            String kode = ((TextView) view.findViewById(R.id.kode)).getText().toString();                           Intent in = new Intent(AksesServerActivity.this, DetailAksesServer.class);            in.putExtra(AR_ID, kode);            startActivity(in);        }    });     }}
