```java
import java.io.*;
import java.util.StringTokenizer;

public class p02gpa {
    public static void main(String[] args) throws IOException {
        String file = "p02-grades.txt";
        BufferedReader br = new BufferedReader(new FileReader(file));
        String rec;
        String fmt = "%-20s %5.2f %8.2f %8.2f%n";
        System.out.println("    Name               GPA  Classes        Hours");
        System.out.println("    ------------------ ---- -------- --------");
        while ((rec = br.readLine()) != null) {
            calcDisp(rec, fmt);
        }
        br.close();
    }

    public static void calcDisp(String rec, String fmt) {
        StringTokenizer tok;
        String name;
        double qualtyPts = 0.0;
        double hours = 0.0;
        double gpa;
        tok = new StringTokenizer(rec, ",|/");
        name = tok.nextToken();
        while (tok.hasMoreTokens()) {
            String letterGrade = tok.nextToken();
            double ptValue;
            double classHours = Double.parseDouble(tok.nextToken());
            switch (letterGrade) {
                case "A":
                    ptValue = 4.0;
                    break;
                case "B":
                    ptValue = 3.0;
                    break;
                case "C":
                    ptValue = 2.0;
                    break;
                case "D":
                    ptValue = 1.0;
                    break;
                default:
                    ptValue = 0.0;
                    break;
            }
            qualtyPts += ptValue * classHours;
            hours += classHours;
        }
        gpa = qualtyPts / hours;
        System.out.printf(fmt, name, gpa, qualtyPts, hours);
    }
}
```

