    public static void main(String[] args) {
        String str = "[00:25.30]";
        StringTokenizer st = new StringTokenizer(str, "[]");
        String first = st.nextToken();
        String second = st.nextToken();
        int minutes = Integer.parseInt(second.substring(0, 2));
        float seconds = Float.parseFloat(second.substring(3, 7));
        minutes += 2;
        seconds += 0.0;
        String result = String.format("[%02d:%05.2f]", minutes, seconds);
        System.out.println(result);
    }
}

