public class DatabaseHelper extends SQLiteOpenHelper {
    private static final String DATABASE_NAME = "mydatabase.db";
    private static final int DATABASE_VERSION = 1;
    private static final String TABLE_NAME = "mytable";
    private static final String COLUMN_POS = "pos";
    private static final String COLUMN_URL = "url";

    public DatabaseHelper(Context context) {
        super(context, DATABASE_NAME, null, DATABASE_VERSION);
    }

    @Override
    public void onCreate(SQLiteDatabase db) {
        db.execSQL("CREATE TABLE " + TABLE_NAME + " (" +
                COLUMN_POS + " INTEGER PRIMARY KEY," +
                COLUMN_URL + " TEXT" +
                ")");
    }

    @Override
    public void onUpgrade(SQLiteDatabase db, int oldVersion, int newVersion) {
        db.execSQL("DROP TABLE IF EXISTS " + TABLE_NAME);
        onCreate(db);
    }

    public void insertUrl(int pos, String url) {
        SQLiteDatabase db = this.getWritableDatabase();
        ContentValues contentValues = new ContentValues();
        contentValues.put(COLUMN_POS, pos);
        contentValues.put(COLUMN_URL, url);
        db.insert(TABLE_NAME, null, contentValues);
    }

    public String loadUrl(int pos) {
        SQLiteDatabase db = this.getReadableDatabase();
        Cursor cursor = db.query(TABLE_NAME,
                new String[]{COLUMN_URL},
                COLUMN_POS + "=?",
                new String[]{String.valueOf(pos)}, null, null, null, null);
        if (cursor != null)
            cursor.moveToFirst();
        String url = cursor.getString(0);
        cursor.close();
        return url;
    }
}
```

