map.put("list1", list1);
map.put("list2", list2);
map.put("list3", list3);
ArrayList<Integer> masterList = new ArrayList<Integer>();
HashSet<Integer> set = new HashSet<Integer>();
for (ArrayList<Integer> list : map.values()) {
    for (Integer i : list) {
        set.add(i);
