byte[] buffer = new byte[1048576];
int i = 0;
int length = -1;

while (true)
{
    int b = is.read();
    if (b == -1)
    {
        success = false;
        break;
    }
    buffer[i] = (byte) b;

    if (i == length)
        break;

                                    if (i == 3)
                                    {
                                                length = buffer[0] | buffer[1] << 8 | buffer[2] << 16 | buffer[3] << 24;

                                        }

