public class JumpIterator implements Iterator<Integer> {
    private Iterator<Integer> it;
    public JumpIterator(List<Integer> list){
        it = list.iterator();
    }
    @Override
    public boolean hasNext() {
        if(!it.hasNext()){
            return false;
        }else{
            it.next();
            return it.hasNext();
        }
    }
    @Override
    public Integer next() {
        return it.next();
    }
}


