Answer:
public UserInfo get_user_by_id(String id) {
    SQLiteDatabase db = this.getReadableDatabase();
    UserInfo userInfo = null;
    Cursor cursor = db.query(TABLE_NAME, new String[]{COLUMN_APP_ID, COLUMN_USER_NAME, COLUMN_PASSWORD}, COLUMN_APP_ID + "=?", new String[]{id + ""}, null, null, null);
    while (cursor.moveToNext()) {
        userInfo = new UserInfo(cursor.getString(cursor.getColumnIndex(COLUMN_APP_ID)), cursor.getString(cursor.getColumnIndex(COLUMN_USER_NAME)), cursor.getString(cursor.getColumnIndex(COLUMN_PASSWORD)));
    }
    return userInfo;
}
