import android.app.Activity;
import android.content.res.Resources;
import android.content.res.XmlResourceParser;
import android.widget.TextView;
public class XMLParse extends Activity {
public void doXML() {
TextView myXmlContent = (TextView)findViewById(R.id.my_xml);
String stringXmlContent;
try { stringXmlContent = getEventsFromAnXML(this); myXmlContent.setText(stringXmlContent); } catch (XmlPullParserException e) { // TODO Auto-generated catch block e.printStackTrace(); } catch (IOException e) { // TODO Auto-generated catch block e.printStackTrace(); }
private String getEventsFromAnXML(Activity activity) throws XmlPullParserException, IOException {
StringBuffer stringBuffer = new StringBuffer();
Resources res = activity.getResources();
XmlResourceParser xpp = res.getXml(R.xml.myxml);
xpp.next();
int eventType = xpp.getEventType();
while (eventType != XmlPullParser.END_DOCUMENT) {
if(eventType == XmlPullParser.START_DOCUMENT) {
stringBuffer.append("--- Start XML ---");
} else if(eventType == XmlPullParser.START_TAG) {
stringBuffer.append("\nSTART_TAG: "+xpp.getName());
} else if(eventType == XmlPullParser.END_TAG) {
stringBuffer.append("\nEND_TAG: "+xpp.getName());
} else if(eventType == XmlPullParser.TEXT) {
stringBuffer.append("\nTEXT: "+xpp.getText());
eventType = xpp.next();
stringBuffer.append("\n--- End XML ---");
return stringBuffer.toString();
} } }
